function Player:addCurExp()
    print("---------addExp---------------------")
    local newExp = self:getPerExpPlus()+self:getCurExp()
    local maxExp = self:getMaxExp()
    if self:isExpFull() and newExp>maxExp then
        newExp = maxExp
    end
    self:setCurExp(newExp)
   -- castSetSkill(self,newExp)
end
