---重置箱子领取状态
function Player:refreshBoxGet(boxId, useTime)
    self.boxReGet = self.boxReGet or {}
    local function refreshGet()
        local d_t = os.time() - useTime
        print("d_t1 : "..tostring(d_t))
        if d_t >= 0 then
            local allBox = self:getBoxData()
            allBox[tostring(boxId)].isGet = false
            local rewardManager = T(Game, "rewardManager")
            rewardManager:refreshBoxByUseTime(self, boxId, os.time())
            LuaTimer:cancel(self.boxReGet[tostring(boxId)])
            self:setBoxData(allBox)
        end
    end
    if self.boxReGet[tostring(boxId)] then
        LuaTimer:cancel(self.boxReGet[tostring(boxId)])
    end
    self.boxReGet[tostring(boxId)] = LuaTimer:scheduleTimer(function()
        refreshGet()
    end, 1000, -1)
end

