---重置箱子月卡
function Player:refreshBoxCard(boxId, payTime)
    print("refreshBoxCard ", 00000)
    self.boxReCard = self.boxReCard or {}
    local function refreshCard()
        local d_t = os.time() - payTime
        print("d_t2 : "..tostring(d_t))
        print("payTime : "..tostring(payTime))
        if d_t >= 0 then
            local buyInfo1 =  self:getPrivilege()
            local allBox1 = self:getBoxData()
            --print("player:getBoxData() 11", Lib.v2s(allBox1))
            --print("buyInfo1 ", Lib.v2s(buyInfo1))
            local config1 = T(Config, "PrivilegeConfig")
            for _, id in pairs(config1:getAllBoxCardId(boxId)) do
                print("getAllBoxCardId v.id "..tostring(id))
                buyInfo1[tostring(id)] = Define.BuyStatus.Unlock
                allBox1[tostring(boxId)].payTime = os.time()
            end
            self:setPrivilege(buyInfo1)
            self:setBoxData(allBox1)
            LuaTimer:cancel(self.boxReCard[tostring(boxId)])
        end
    end
    if self.boxReCard[tostring(boxId)] then
        LuaTimer:cancel(self.boxReCard[tostring(boxId)])
    end
    self.boxReCard[tostring(boxId)] = LuaTimer:scheduleTimer(function()
        refreshCard()
    end, 1000, -1)
end

